                  >
                    Login
                  </h3>

                  <fg-input
                    v-model="email"
                    addon-left-icon="nc-icon nc-single-02"
                    placeholder="Username"
                    style="margin-left: -8px"
                  ></fg-input>

                  <fg-input
                    v-model="password"
                    addon-left-icon="nc-icon nc-key-25"
                    placeholder="Password"
                    type="password"
                    style="margin-left: -8px"
                  ></fg-input>

                  <br />

                  <!-- <p-checkbox>Subscribe to newsletter</p-checkbox> -->

                  <p-button
                    native-type="button"
                    slot="footer"
                    type="info"
                    round
                    block
                    class="mb-3"
                    @click="onSubmit"
                    >LOG IN</p-button
