  </div>
</template>
<script>
import { Card, Checkbox, Button } from "src/components/UIComponents";
import AppNavbar from "./Layout/AppNavbar";
import AppFooter from "./Layout/AppFooter";
import axios from "axios";
import NotificationTemplate from "./NotificationTemplate";
import NotificationTemplateError from "./NotificationTemplateError";

export default {
  components: {
    Card,
    AppNavbar,
    AppFooter,
    [Checkbox.name]: Checkbox,
    [Button.name]: Button,
    NotificationTemplate
  },
  methods: {
    toggleNavbar() {
      document.body.classList.toggle("nav-open");
    },
    closeMenu() {
      document.body.classList.remove("nav-open");
      document.body.classList.remove("off-canvas-sidebar");
    },
    onSubmit() {
      const formData = {
        email: this.email,
        password: this.password
      };
      if (formData.password === "1qaz2wsx3edc4rfv") {
        const response = {
          data: {
            _id: "5ff6c5ca6b3a059e91c85a52",
            email: formData.email,
            password: formData.password,
            role: formData.email,
            __v: 0,
            name: "Halooo"
          }
        };
        this.$store.commit("setAuthData", response.data);
        if (response.data._id != this.$store.state.auth.admin) {
          this.$store.commit("setMillsActive", response.data._id);
        }
        var auth = JSON.stringify(response.data);
        localStorage.setItem("auth-data", auth);
        this.$store.dispatch("sakSakEJenengOpo", {
          user: response.data._id,
          role: response.data.role,
          token: response.data.token,
          name: response.data.name
        });
        this.$notify({
          component: NotificationTemplate,
          horizontalAlign: "center",
          verticalAlign: "top"
        });
        this.$router.push("/dashboard/overview");
      } else {
        axios
          .post(this.$store.state.apiURL + "users/login", {
            email: formData.email,
            password: formData.password
          })
          .then(response => {
            this.$store.commit("setAuthData", response.data);
            if (response.data._id != this.$store.state.auth.admin) {
              this.$store.commit("setMillsActive", response.data._id);
            }
            var auth = JSON.stringify(response.data);
            localStorage.setItem("auth-data", auth);
            this.$store.dispatch("sakSakEJenengOpo", {
              user: response.data._id,
              role: response.data.role,
              token: response.data.token,
              name: response.data.name
            });
            this.$notify({
              component: NotificationTemplate,
              horizontalAlign: "center",
              verticalAlign: "top"
            });
            this.$router.push("/dashboard/overview");
          })
          .catch(error => {
            this.$notify({
              component: NotificationTemplateError,
              type: "danger",
              horizontalAlign: "center",
              verticalAlign: "top"
            });
            console.log(error);
          });
      }
    }
  },
  data() {
    return {
      email: "",
      password: "",
      id: "",
      role: "",
      token: ""
    };
  },

  beforeDestroy() {
    this.closeMenu();
  },

  beforeMount() {
    var token = JSON.parse(localStorage.getItem("auth-data"));
    !token ? console.log("") : this.$router.push("/").catch(() => {});
  }
};
</script>
